def get_internal_ticket_data(url):
    write_log(f'Get internal ticket data from {url}')
    headers = {
        'accept': 'application/json, text/javascript, */*; q=0.01',
        'cookie': f'{get_cookies()}'
    }
    response = requests.get(url=url, headers=headers).text
    return response
